const items = $input.all();

function getBrazilDateString() {
  const now = new Date();
  const offsetMs = -3 * 60 * 60 * 1000;
  const brTime = new Date(now.getTime() + offsetMs);
  const pad = n => String(n).padStart(2, '0');
  return `${brTime.getFullYear()}-${pad(brTime.getMonth() + 1)}-${pad(brTime.getDate())}`;
}

const todayStr = getBrazilDateString();

return items.map(item => {
  const a = item.json;
  const isToday = a.appointment_date === todayStr;

  return {
    json: {
      doctor_id: a.doctor_id,
      status: a.status,
      appointment_date: a.appointment_date,
      isToday
    }
  };
});

const items = $input.all();

const doctorStats = {};

for (const item of items) {
  const a = item.json;
  if (!a.isToday) continue; 

  const doctorId = a.doctor_id || 'unknown';
  if (!doctorStats[doctorId]) {
    doctorStats[doctorId] = { completed: 0, no_show: 0, total_today: 0 };
  }

  doctorStats[doctorId].total_today++;

  const status = (a.status || '').toLowerCase().trim();

  if (status === 'completed') doctorStats[doctorId].completed++;
  if (status === 'no show' || status === 'no_show') doctorStats[doctorId].no_show++;
}

const result = Object.entries(doctorStats).map(([doctor_id, stats]) => ({
  json: {
    doctor_id,
    completed: stats.completed,
    no_show: stats.no_show,
    total_today: stats.total_today
  }
}));

return result;
